DROP TABLE IF EXISTS `goods`;
CREATE TABLE `goods` (
  `id` int(10) NOT NULL,
  `name` varchar(100) NOT NULL,
  `description` text NOT NULL,
  `mini-foto` varchar(100) NOT NULL,
  `foto` varchar(100) NOT NULL,
  `category` int(3) NOT NULL,
  `cost` int(10) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

INSERT INTO `goods` (`id`, `name`, `description`, `mini-foto`, `foto`, `category`, `cost`) VALUES
(1, 'Сыр Швейцарский Emmi', 'Сыр Швейцарский / Switzerland от Emmi (Швейцария) - твёрдый швейцарский сыр из свежего коровьего молока без добавок, красителей и ароматизаторов.\r\n\r\nШвейцарский сыр от Эмми с ароматной миндальной ноткой и нежным сливочным вкусом – отличная самостоятельная закуска и ингредиент для приготовления салатов, соусов и других блюд.\r\n\r\nМассовая доля жира в сухом веществе - 45%.\r\nСрок хранения - 180 дней.\r\nСтоимость товара за 1 кг - 1000 руб.\r\nЦена указана для единицы товара среднего веса ~ 5,5 кг.\r\nФактический вес единицы товара незначительно повлияет на его стоимость (+ / - 7%).', 'res/tovar/mini_6284111900.png', 'res/tovar/8989111900.png', 1, 11900),
(2, 'Сыр Пармиджано Реджано Parmareggio DOP', 'Сыр Пармиджано Реджано (пармезан) Италия - натуральный твердый выдержанный сыр из коровьего молока от итальянского производителя Parmareggio заботливо завернутый в пергамент, что исключает ошибку определения фактического срока выдержки и подтверждается торговой маркой.\r\n\r\nСыр Parmigiano Regiano DOP производится по традиционному рецепту провинции Мантова, на правом берегу реки По.\r\n\r\nАромат - сладковатый с фруктовыми нотками. Вкус - напоминающий ананас, но исключительно яркий и насыщенный.\r\n\r\nПармеджано Реджано подают с аперитивами, фруктами (виноград, груша) и орехами. Перед подачей к столу рекомендуется подержать при комнатной температуре.\r\n\r\nВ кулинарии сыр используется при создании неповторимого вкуса и аромата соусов, салатов, супов, вторых блюд.\r\n\r\nВыдержка - 30 месяцев.\r\n\r\nМассовая доля жира в сухом веществе - 32%.\r\n\r\nСостав: пастеризованное коровье молоко, соль пищевая, сычужный фермент животного происхождения.\r\n\r\nПищевая и энергетическая ценность (калорийность), содержание в 100 г продукта:\r\nбелки - 33 г, жиры - 28,4 г, углеводы - 0 г, сахар - 0 г, соль - 1,4 г, 392 Ккал / 1633 Кдж\r\nвитамин А - 270 мг, кальций - 1160 мг, фосфор 680 мг\r\n\r\nХранить при температуре: от +4 до +8 °C\r\n\r\nВес нетто: 500 г.', 'res/tovar/mini_497011680.png', 'res/tovar/606611680.png', 1, 1680),
(3, 'Сыр овечий Пасторино Сардо CAO', 'Сыр овечий Пасторино Сардо производства Италия - натуральный сычужный полутвёрдый сыр из овечьего молока от итальянского производителя CAO Formaggi (Ористано, регион Сардиния).\r\n\r\nPastorino Sardo - овечий сыр, отражающий сырные традиции Сардинии, в коричневой кожуре, с нежным вкусом и ароматным сырным тестом белого цвета с небольшими сырными дырочками. Сыр Пасторино Сардо великолепно сочетается с хлебом, с традиционными сардинскими лепешками Carasau и красным вином.\r\n\r\nСостав: овечье молоко, соль.\r\n\r\nПищевая и энергетическая ценность (калорийность), содержание в 100 г продукта:\r\nбелки - 24 г, жиры - 34 г, углеводы - 0 г, сахар - 0,5 г, соль - 1,7 г, 402 Ккал / 1666 Кдж\r\n\r\nУпаковано в вакуумный пакет\r\nХранить при температуре: от +4 до +8 °C\r\n\r\nТовар продается на вес.\r\n\r\nЦена указана за условную единицу товара весом 690 г.', 'res/tovar/mini_166222050.png', 'res/tovar/310622050.png', 2, 2050),
(4, 'Сыр Марешаль Emmi', 'Головка сыра Марешаль (Le Marechal) от Emmi (Швейцария) - твёрдый швейцарский сыр из сырого коровьего молока, который созревает в течение 5 месяцев.\r\n\r\nСыр Марешаль обладает необычный пряный аромат и пикантным вкусом, т.к. в процессе созревания его натирают прованскими травами.\r\n\r\nСостав: непастеризованное коровье молоко, молочнокислые бактерии, поваренная соль, сычужный фермент, прованские травы.\r\n\r\nМассовая доля жира в сухом веществе - 50%.\r\nПищевая ценность на 100 г: белки - 26,5 г, жиры - 33 г, углеводы - 0 г.\r\nЭнергетическая ценность на 100 г - 403 ккал.\r\nСрок хранения - 60 дней.\r\nСтоимость товара за 1 кг - 1660 руб.\r\nЦена указана для единицы товара среднего веса ~ 6 кг.\r\nФактический вес единицы товара незначительно повлияет на его стоимость (+ / - 7%).', 'res/tovar/mini_428122350.png', 'res/tovar/3842122350.png', 1, 22350),
(5, 'Сыр из молока Буйволиц Качотта', 'Сыр из молока буйволиц Качотта Буфала - натуральный выдержанный сычужный сыр от итальянского производителя Botalla Srl (Пьемонт, Италия).\r\n\r\nГоловка сыра имеет маленький размер, что связано с его названием. Caciotta с итальянского означает сырок (маленький сыр).\r\n\r\nСыр Caciotta di Bufala Botalla обладает тонким вкусом и приятным послевкусием.\r\n\r\nСыр отлично сочетается с хлебом, свежими помидорами или оливками, подходит для приготовления салатов, горячих бутербродов, т.к. хорошо плавится и других блюд. Качотта Буфала дополнит самую изысканную сырную тарелку.\r\n\r\nСостав: молоко буйволиц, соль, сычужный фермент.\r\n\r\nХранить при температуре: от +4 до +8 °C\r\n\r\nЦена указана за единицу товара среднего веса ~ 600 г.\r\n\r\nОкончательная стоимость товара уточняется в соответствии с фактическим весом.', 'res/tovar/mini_226432050.png', 'res/tovar/783232050.png', 3, 2050),
(6, 'Сыр Жура Монтань Margot Fromages', '1/2 головки сыра Жура Монтань от Margot Fromages производства Швейцария - натуральный твёрдый сычужный сыр из молока коров, пасущихся на альпийских лугах, произведённый в горах Жура, на высоте 1000 метров над уровнем моря, в регионе Сэньлежье.\r\n\r\nСыр Jura Montagne произведен непромышленным способом, созревает 6 месяцев в пещерах, где его регулярно обмывают и переворачивают, в результате чего образуется мытая корочка, а сыр приобретает чудесный вкус.\r\n\r\nВыдержка - более 6 месяцев.\r\n\r\nМассовая доля жира в сухом веществе - 52%.\r\n\r\nСрок хранения - 180 дней\r\nХранить при температуре: от +2 до +8 °C\r\n\r\nПищевая и энергетическая ценность (калорийность), содержание в 100 г продукта:\r\nбелки - 27 г, жиры - 32 г, углеводы - 0 г, 387 Ккал\r\n\r\nТовар продается на вес.\r\n\r\nЦена указана за половинку сырной головки весом примерно 5 кг.', 'res/tovar/mini_4495113700.png', 'res/tovar/2432113700.png', 1, 13700),
(7, 'Сыр Бернер Margot Fromages', 'Сыр Бернер Margot Fromages 12 мес. Головка ~ 6 кг (Швейцария)\r\n\r\nСыр Бернер Berner Margot Fromages производства Швейцария - натуральный полутвёрдый сычужный сыр из сырого коровьего молока от швейцарского производителя Марго Фромаж (кантон Во).\r\n\r\nСыр Бернер произведён на сыроварне, расположенной на горе Фризенхаус в Эмментале, его вызревание длится 12 месяцев в погребе. Сыр обладает весьма высокой питательностью, считается самостоятельным блюдом и отлично сочетается с лёгким белым вином.\r\n\r\nВыдержка - 12 месяцев.\r\n\r\nМассовая доля жира в сухом веществе - 45%.\r\n\r\nСрок хранения - 180 дней\r\nХранить при температуре: от +4 до +8 °C\r\n\r\nТовар продается на вес.\r\n\r\nЦена указана за головку весом примерно 6 кг.', 'res/tovar/mini_937219320.png', 'res/tovar/3701219320.png', 2, 19320);


ALTER TABLE `goods`
  ADD PRIMARY KEY (`id`),
  ADD UNIQUE KEY `name` (`name`);


ALTER TABLE `goods`
  MODIFY `id` int(10) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=8;
